mapper.select:
	    select  a.*,b.cname,c.avatar as by1  
        from coursemessage a  left join course b on a.cid=b.cid  	
		left join students c on a.sno=c.sno


		    and a.id = #{id}


		    and a.cid = #{cid}


		    and a.mtitle = #{mtitle}


		    and a.sno = #{sno}


		    and a.addtime = #{addtime}


		    ${condition} 




    order by ${sort} id desc


			limit #{offset} ,#{pageSize}

